                //         firstName: 'Trần',
                //         lastName: 'Lê Minh'
                //     },
                //     createdByEmail: '',
                //     createdTime: new Date()
                // },
                // {
                //     TemplateGroupTaskId: '2',
                //     nameTemplateGroupTask: 'Template 2',
                //     listProcess: [
                //         {
                //             processName: 'Quá trình 1',
                //             description: '',
                //             columnSetting: {
                //                 columnSettingId: '1',
                //                 color: "#000000",
                //                 colorText: '#000000',
                //                 ColorHeader: '#000000'
                //             }
                //         }
                //     ],
                //     createdByEmail: '',
                //     createdBy: {
                //         email: 'tlminh10300@gmail.com',
                //         firstName: 'Trần',
                //         lastName: 'Lê Minh'
                //     },
                //     createdTime: new Date()
                // }
            //],
            listTempProcess: [],
            newTemplate: {
                templateGroupTaskId: uuid.v1(),
                nameTemplateGroupTask: '',
                listProcess: [
                    {
                        processId: uuid.v1(),
                        processName: '',
                        description: '',
                        columnSetting: {
                            columnSettingId: uuid.v1(),
                            color: "#00ffff",
                            colorText: '#000000',
                            colorHeader: '#2196f3'
                        },
                        sortOrder: 0,
                        isNewProcess: true,
                        isEditing: true
                    }
                ],
                createdBy: {},
                createdByEmail: '',
                createdTime: null
            }
        }
    }
